DataType.ClientEvent:
if (arg.property == "isprimaryKey") {
    var form = this.id("autoFormColumn");
    var defaultValue = form.getElement("defaultValue");
    
    var tree = this.id("dataTreeDbInfo");
    var currentNode = tree.get("currentNode");
    
    
    if (arg.newValue) {
        if (currentNode) {
            var level = currentNode.get("level");
            if (level == 2) {
                var parentNode = currentNode.get("parent");
                var parentEntity = parentNode.get("data");
                if (parentEntity.get("dbType") == "DB2") {
					arg.entity.set("isprimaryKey", false);
                    return dorado.MessageBox.alert("${res["dataTypeColumn.onDataChange.db2AlertMessage"]}");
                }else{
					// defaultValue.set("readOnly", true);
                     arg.entity.set("isnullAble", false);
				}
            }
        }
        
       
    }
    else {
        //defaultValue.set("readOnly", false);
        
    }
}
else 
    if (arg.property == "isnullAble") {
        if (arg.newValue) {
            arg.entity.set("isprimaryKey", false);
        }
    }

View.ClientEvent:
this.expandNode = function(){
    var tree = this.id("dataTreeDbInfo");
    var currentNode = tree.get("currentNode");
	if(!currentNode.get("expanded")){
	    currentNode.expand();
	}
}
self.createConnection = function(){
    var dialog = this.id("dialogDbInfo");
    var data = dataSetDbInfo.getData();
    dialogDbInfo.show();
    var current = data.insert({
        name: "[Connection Name] "
   	});
    dataTreeDbInfo.set("currentEntity", current);
}

self.$setCurrentNode = function(node){
    dataTreeDbInfo.set("currentNode", node);
	
}

self.flushTabData = function(){
    var currentNode = dataTreeDbInfo.get("currentNode");
    var level = currentNode.get("level");
    var tab = tabControlContext.get("currentTab");
    var name = tab.get("name");	
    if (2 == level) {
        var entity = currentNode.get("data");
        var dbInfoId = entity.get("dbInfoId");
        var tableName = entity.get("tableName");
		
        if (name == "dataTab") {
            var path = "bdf3.dbconsole.view.datatab.DataTabMaintain.d?type=1&dbInfoId=" + dbInfoId + "&tableName=" + tableName + "";
            tab.set("path", path);
        } else if (name == "columnTab") {
            dataSetColumn.set("parameter", {
                dbInfoId: dbInfoId,
                tableName: tableName
            });
            dataSetColumn.flushAsync();
       }
    } else if (1 == level) {
        var entity = currentNode.get("data");
        var dbInfoId = entity.get("id");
        if (dbInfoId) {
            if (name == "dataTab") {
                tab.set("path", "bdf3.dbconsole.view.messagetab.MessageTabMaintain.d");
            } else if (name == "columnTab") {
                dataSetColumn.set("parameter", {
                    dbInfoId: dbInfoId,
                    tableName: ''
                });
                dataSetColumn.flushAsync();
            }
        }
	} else {
		 if (name == "dataTab") {
		 	tab.set("path", "bdf3.dbconsole.view.messagetab.MessageTabMaintain.d");
		 }
	}
    
}
self.$createSql = function(){
	var oldSql= sourceEditor.getCodeMirror().getValue();
    var currentNode = dataTreeDbInfo.get("currentNode");
    var level = currentNode.get("level");
    if (2 == level) {
        var entity = currentNode.get("data");
        var tableName = entity.get("tableName");
        var s="select * from " + tableName;
		if (oldSql) {
			sourceEditor.getCodeMirror().setValue(oldSql + "\n"+s);
		} else {
			sourceEditor.getCodeMirror().setValue(s);
		}
    }
}

DataSetDropDown.ClientEvent:
var selectedValue=arg.selectedValue;
var data=this.id("dataSetDbInfo").getData("#");
var tablePrimaryKey=data.get("url");
var primaryKeyType=data.get("dbType");
if("Oracle"==selectedValue){
	data.set("driverClass","oracle.jdbc.driver.OracleDriver");
	data.set("url","jdbc:oracle:thin:@localhost:1521:tempdb");
	data.set("username","orcl");
}else if("SqlServer"==selectedValue){
	data.set("driverClass","net.sourceforge.jtds.jdbc.Driver");
	data.set("url","jdbc:jtds:sqlserver://localhost:1433/master");
	data.set("username","sa");
}else if("MySQL"==selectedValue){
	data.set("driverClass","com.mysql.jdbc.Driver");
	data.set("url","jdbc:mysql://localhost:3306/tempdb?useUnicode=true&amp;characterEncoding=UTF-8");
	data.set("username","root");
}else if("DB2"==selectedValue){
	data.set("driverClass","com.ibm.db2.jcc.DB2Driver");
	data.set("url","jdbc:db2://localhost:50000/tempdb");
}else if("H2"==selectedValue){
	data.set("driverClass","org.h2.Driver");
	data.set("url","jdbc:h2:~/test");
	data.set("username","sa");
}

var selectValue = arg.selectedValue;
if (selectValue) {
    var form = this.id("autoFormColumn");
    var columnSize = form.getElement("columnSize");
    var entity = this.id("dataSetColumn").getData("#");
    if (selectValue.indexOf("DATE") != -1 || selectValue.indexOf("TIME") != -1) {
        columnSize.set("readOnly", true);
        entity.set("columnSize", null);
    }
    else {
        columnSize.set("readOnly", false);
    }
}

