var action = ajaxActionAlertTableName;
var tree = dataTreeDbInfo;
var currentNode = tree.get("currentNode");

var entity = currentNode.get("data");
var level = currentNode.get("level");
if (level == 2) {
	var tableName = entity.get("tableName");
	var dbInfoId = entity.get("dbInfoId");
}
if (tableName && dbInfoId) {	
	dorado.MessageBox.prompt("${res['menuItemRename.onClick.promptInfo']}", {
		defaultText: tableName,
	 	callback: function(text){				 	
			if (!text) {
				dorado.MessageBox.alert("${res['menuItemRename.onClick.nullAlertMessage']}");
				return;
			}
			if (text.toLowerCase() == tableName.toLowerCase()) {
				dorado.MessageBox.alert("${res['menuItemRename.onClick.duplicatelAlertMessage']}");
				return ;
			}
			entity.set("tableName", text);
				action.set("parameter", {
				dbInfoId: dbInfoId,
				tableName: tableName,
				newTableName: text
			}).execute();
		 }
	});
}

