var tree = dataTreeDbInfo;
var currentNode = tree.get("currentNode");
var entity = currentNode.get("data");
var level = currentNode.get("level");
if (level == 1) {
    var dbInfoId = entity.get("id");
}
if (dbInfoId) {
    dorado.MessageBox.prompt("${res['menuItemCreateTable.onClick.promptInfo']}", {
        defaultText: "",
        callback: function(text){
            if (!text) {
                dorado.MessageBox.alert("${res['menuItemCreateTable.onClick.nullTableNameAlertMessage']}");
                return;
            }
            action.set("parameter", {
                dbInfoId: dbInfoId,
                tableName: text
            }).execute(function() {     
                var currentEntity = currentNode.get("data");
                var newEntity = currentEntity.createChild("tableInfos", {
                    tableName: text,
                    dbInfoId: dbInfoId
                })
                tree.set("currentEntity", newEntity);
                dataSet.set("parameter", {
                    dbInfoId: dbInfoId,
                    tableName: text
                });
                dataSet.flush();
            });
        }
    });
}

